function eachText(){$.fn.writeText=function(t){var e=t.split(""),n=0,i=this;setInterval(function(){n<e.length&&i.text(i.text()+e[n++])},70)},$(".intro-text span").each(function(t){var e=$(this),n=timer+t*timer;setTimeout(function(){text=e.text(),e.addClass("active").empty().writeText(text),setTimeout(function(){e.removeClass("active")},timer-500)},n)})}var timer=6e3;$(function(){var t=$(".intro-text span").length;eachText(),setInterval(function(){eachText()},1.1*timer*t)});